INSERT INTO `tbl_category_product` (`category_id`, `category_name`, `category_desc`, `category_status`, `created_at`, `updated_at`) VALUES
(34, 'Sữa trái cây', 'Sữa trái cây là sự kết hợp của sữa và nước trai cây với nhiều hương vị đa dạng, độc đáo cho người dùng. Thành phần chủ yếu có trong sản phẩm có nguồn gốc từ sữa', 1, NULL, NULL),
(35, 'Sữa chua', 'Sữa chua uống, sữa chua men sống hỗ trợ tăng đề kháng, tiêu hóa, đẹp da, đẹp dáng. Bán hàng uy tín, chất lượng, giao hàng nhanh đúng giờ.', 1, NULL, '2024-12-03 14:22:29'),
(37, 'Sữa tươi', 'Sữa tươi cung cấp nhiều vitamin và khoáng chất cho cơ thể. Trong sữa tươi cực kỳ giàu vitamin B12, vitamin B2 (hay còn gọi là Riboflavin), vitamin D,...', 1, NULL, NULL),
(59, 'Sữa bột', 'Sữa bột hay cũng còn gọi là sữa công thức, là 1 sản phẩm sản xuất từ sữa ở dạng bột khô. Cách thực hiện là làm bốc hơi sữa sau đó để khô và tán, nghiền thành dạng bột.', 1, NULL, '2024-12-03 14:22:25'),
(60, 'Sữa ngũ cốc', 'Ngũ cốc là tên gọi chung của loại thực phẩm được làm từ 5 loại hạt khác nhau được dân gian và Y học hiện đại nghiên cứu và khẳng định mang đến nhiều giá trị dinh dưỡng kể cả người già và trẻ nhỏ', 1, NULL, NULL),
(61, 'Sữa đặc', 'Sữa đặc hay sữa cô đặc là sữa bò đã hút hết nước. Loại sữa này thường ở dạng sữa đặc có đường', 1, NULL, '2024-12-03 14:22:32'),
(62, 'Sữa hạt', NULL, 1, NULL, NULL),
(156, 'Trà sữa', NULL, 1, NULL, NULL);
INSERT INTO `tbl_comment` (`comment_id`, `comment`, `comment_name`, `comment_product_id`, `customer_id`, `comment_parent_comment`, `created_at`, `updated_at`) VALUES
(446, 'Mùi vị béo ngậy nhưng không quá ngọt, phù hợp với cả người lớn và trẻ em. Gia đình mình rất thích!', 'Ava Davis', 164, 159, 0, '2024-11-12 07:08:29', '2024-11-12 07:08:29'),
(469, 'Mình không thích vị của sữa này lắm, có cảm giác hơi nhạt và không béo như những loại sữa bột khác. Có thể sẽ không mua lại', 'Emma Johnson', 164, 155, 0, '2024-11-14 12:07:31', '2024-11-14 12:07:31'),
(473, 'Giá cao hơn so với những loại sữa bột không đường khác mà chất lượng không có gì đặc biệt. Mình thấy hơi thất vọng về sản phẩm.', 'John Smith', 164, 154, 0, '2024-11-14 12:13:53', '2024-11-14 12:13:53'),
(474, 'Mình dùng sữa bột này mỗi sáng với cà phê. Không đường, rất phù hợp với khẩu vị của mình. Sữa không quá béo, nhẹ nhàng nhưng vẫn thơm ngon.', 'Liam Williams', 164, 156, 0, '2024-11-14 12:15:01', '2024-11-14 12:15:01'),
(475, 'Đã sử dụng sản phẩm này một thời gian và mình phải nói là rất ưng ý! Sữa có vị thơm tự nhiên, không quá ngọt, rất dễ uống và đặc biệt là không có chất tạo ngọt hay phụ gia, điều này làm mình cảm thấy yên tâm khi sử dụng. Mình đã thử pha sữa với cà phê, vừa giúp tạo ra một ly cà phê sữa thơm ngon mà lại không lo tăng lượng đường trong cơ thể. Hơn nữa, sữa bột này rất mịn, dễ tan, không bị vón cục hay sạn như những loại sữa bột khác mà mình đã thử trước đây. Chất lượng sản phẩm cũng rất ổn vì DEVONDALE là thương hiệu nhập khẩu từ Úc, nên mình hoàn toàn tin tưởng vào nguồn gốc và chất lượng. Mình sẽ tiếp tục sử dụng và chắc chắn sẽ giới thiệu sản phẩm này cho bạn bè và gia đình.', 'Olivia Brown', 164, 157, 0, '2024-11-14 12:16:29', '2024-11-14 12:16:29'),
